import React from "react";
import "./dropdown.scss";
import {
  Menu,
  MenuButton,
  MenuList,
  MenuItemOption,
  MenuOptionGroup,
  MenuDivider,
} from "@chakra-ui/react";
import { Button } from "@chakra-ui/react";

const STYLES = ["dropdown-dark", "dropdown-light"];

const Dropdown = ({ dropdownStyle }) => {
  const checkDropdownStyle = STYLES.includes(dropdownStyle)
    ? dropdownStyle
    : STYLES[0];
  return (
    <Menu closeOnSelect={false}>
      <MenuButton
        as={Button}
        colorScheme="blue"
        className={`${checkDropdownStyle}`}
      >
        Sort by : Most Upvotes
      </MenuButton>
      <MenuList minWidth="240px">
        <MenuOptionGroup defaultValue="asc" title="Order" type="radio">
          <MenuItemOption value="asc">Most Upvotes</MenuItemOption>
          <MenuDivider />
          <MenuItemOption value="desc">Least Upvotes</MenuItemOption>
          <MenuDivider />
          <MenuItemOption value="desc">Most Comments</MenuItemOption>
          <MenuDivider />
          <MenuItemOption value="desc">Least Comments</MenuItemOption>
        </MenuOptionGroup>
      </MenuList>
    </Menu>
  );
};

export default Dropdown;
