import React, { useState } from "react";
import "./getsorted.scss";

const Getsorted = ({ selected, setSelected }) => {
  const [isOpen, setIsOpen] = useState(false);
  return (
    <>
      <div className="dropdown">
        <div className="dropdown-btn" onClick={(e) => setIsOpen(!isOpen)}>
          Sort by :{" "}
        </div>
        {isOpen && (
          <div className="dropdown-content">
            <div
              onClick={(e) => setSelected(e.target.text)}
              className="dropdown-item"
            >
              Most Upvotes
            </div>
            <hr></hr>
            <div className="dropdown-item">Least Upvotes</div>
            <hr></hr>

            <div className="dropdown-item">Most Comments</div>
            <hr></hr>

            <div className="dropdown-item">Least Comments</div>
          </div>
        )}
      </div>
    </>
  );
};

export default Getsorted;
